import React, {useState} from 'react';
import styled from 'styled-components';
import Picker from 'emoji-picker-react';
import {IoMdSend} from 'react-icons/io';



const ChatInput = () => {
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const [msg, setMsg] = useState("");

    const handleEmojiPickerHideShow = () =>{
        setShowEmojiPicker(!showEmojiPicker);
        console.log("gola")
    }

    const handleEmojiClick = (event,emoji)=>{
        let message = msg;
        message += emoji.emoji;
        setMsg(message)

    }

  return (
    <Container>
        <div className="button-container">
            <button onClick={handleEmojiPickerHideShow} className="emoji">
                emojis{showEmojiPicker && <Picker onEmojiClick={handleEmojiClick} />}
            </button>
            <form className="input-container">
                <input type="text" placeholder="Type your message here" value={msg} onChange={(e)=>setMsg(e.target.value)}/>
                <button className='submit'>
                    <IoMdSend/>
                </button>
            </form>
        </div>
    </Container>
  )
}
const Container = styled.div`
  display:grid;
  grid-template-columns: 5% 95%
  align-items:center;
  background-color: #080420;
  padding: 0 2rem;
  @media screen and (min-width:720px)and (max-width: 1080px){
      padding: 0 1rem;
      gap: 1rem;
  }
  .button-container{
      display: flex;
      align-items:center;
      color: white;
      gap: 1rem;
      button{
          padding: 1rem;
          border: none;
          border-radius: 2rem;
      }
      .emoji{
          position: relative;
          svg{
              font-size: 1.5rem;
              color: #ffff00c8;
              cursor: pointer;
          }
          .emoji-picker-react{
              position:absolute;
              top: -350px;
              background-color: #080420;
              box-shadow: 0 5px 10px #9a86f3;
              .emoji-scroll-wrapper::-webkit-scrollbar{
                  border-color: #9a86f3;
                  width: 5px ;
                  &-thumb{
                      background-color: #9a86f3;
                  }
              }
              .emoji-categories{
                  button{
                      filter: contrast(0)
                  }
              }
              .emoji-search{
                  background-color: transparent;
                  border-color: #9a86f3;
              }
              .emoji-group:before{
                  background-color: #080420;
              }
          }
      }
  }
  .input-container{
      width:100% ;
      border-radius:2rem;
      display: flex;
      align-items: center;
      gap: 2rem;
      background-color: #ffffff34;
      input{
          width:90% ;
          height: 60%;
          background-color: transparent;
          color:white;
          border: none;
          padding-left: 1rem;
          font-size: 1.2rem;

          &::selection{
              background-color: #9a86f3;
          }
          &:focus{
              outline: none
          }
      }
      button{
          padding: 0.3rem 2rem;
          border-radius: 2rem;
          display: flex;
          justify-conten: center;
          align-items:center;
          background-color: #9a86f3;
          border:none;
          @media screen and (min-width: 720px) and (max-width: 1080px){
              padding: 0.3rem 1rem;
              svg{
                  font-size: 1rem;
              }
          }
          svg{
              font-size: 2rem;
              color: white;
          }
      }
  }

`;
export default ChatInput
